import React, { useState } from 'react';
import './App.css';
import CountdownTimer from './components/CountdownTimer';
import Snowflake from './components/Snowflake';

const App = () => {
    const [score, setScore] = useState(0);

    const handleSnowflakeClick = () => {
        setScore(prevScore => prevScore + 1); // Increment score
    };

    const createSnowflakes = (num) => {
        return Array.from({ length: num }, (_, index) => {
            const left = Math.random() * 100; // Random horizontal position
            const animationDuration = 5 + Math.random() * 10; // Duration between 5 to 15 seconds
            const animationDelay = 0; // No delay

            return (
                <Snowflake 
                    key={index} 
                    id={index} 
                    left={left} 
                    duration={animationDuration} 
                    delay={animationDelay} 
                    onClick={handleSnowflakeClick} 
                />
            );
        });
    };

    const snowflakes = createSnowflakes(50); // Create 50 snowflakes

    return (
        <div className="App">
            <header className="App-header">
                <CountdownTimer />
            </header>
            <p>Click on the snowflakes to increase your score!</p>
            <div>Score: {score}</div>
            {snowflakes}
        </div>
    );
};

export default App;
