// Snowflake.js
import React from 'react';

const Snowflake = ({ id, left, duration, delay }) => {
    const style = {
        position: 'absolute',
        left: `${left}%`,
        top: '-5%', // Start above the viewport
        animationDuration: `${duration}s`,
        animationDelay: `${delay}s`
    };

    // Call the onClick function passed as a prop when a snowflake is clicked
    return <div className="snowflake" id={`snowflake-${id}`} style={style} onClick={() => onClick()}>❄️</div>;
};

export default Snowflake;
