import React, { useState } from 'react';
import Snowflake from './Snowflake';
import CountdownTimer from './CountdownTimer'; // Assuming you have this component

const App = () => {
    const [score, setScore] = useState(0);

    const handleSnowflakeClick = () => {
        setScore(prevScore => prevScore + 1);
    };

    const createSnowflakes = (num) => {
        return Array.from({ length: num }, (_, index) => {
            const left = Math.random() * 100;
            const animationDuration = 3 + Math.random() * 5;
            const animationDelay = 0;

            return (
                <Snowflake 
                    key={index} 
                    id={index} 
                    left={left} 
                    duration={animationDuration} 
                    delay={animationDelay} 
                    onClick={handleSnowflakeClick} 
                />
            );
        });
    };

    const snowflakes = createSnowflakes(50); // Create 50 snowflakes

    return (
        <div className="App">
            <CountdownTimer />
            <p>Click on the snowflakes to increase your score!</p>
            <div>Score: {score}</div>
            {snowflakes}
        </div>
    );
};

export default App;
