import React, { useState, useEffect } from 'react';
import Snowflake from './Snowflake';

const CountdownTimer = () => {
    const [timeLeft, setTimeLeft] = useState(/* ... */);
    const [score, setScore] = useState(0);
    const [snowflakesVisibility, setSnowflakesVisibility] = useState(Array(50).fill(true)); // Visibility state for each snowflake


    // ... existing countdown logic ...

    const handleSnowflakeClick = (id) => {
        setScore(prevScore => prevScore + 1);
        // Update visibility of the clicked snowflake
        setSnowflakesVisibility(prevVisibility => prevVisibility.map((isVisible, index) => index === id ? false : isVisible));
    };

    const createSnowflakes = (num) => {
        return Array.from({ length: num }, (_, index) => {
            const left = Math.random() * 100;
            const animationDuration = 5 + Math.random() * 10;
            const animationDelay = 0;

            return (
                <Snowflake 
                    key={index} 
                    id={index} 
                    left={left} 
                    duration={animationDuration} 
                    delay={animationDelay} 
                    onClick={handleSnowflakeClick} 
                />
            );
        });
    };

    const snowflakes = createSnowflakes(50);

    return (
        <div>
            <h1>❄️KBS Winter Break!❄️</h1>
            {/* Countdown display */}
            <p>Click on the snowflakes to increase your score!</p>
            <div>Score: {score}</div>
            {snowflakes}
        </div>
    );
};

export default CountdownTimer;
